import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Home = () => {
    const accessToken = localStorage.getItem('accessToken');
    const navigate = useNavigate();
    const [photo, setPhoto] = useState('');

    useEffect(() => {
        // Make a GET request to fetch data from the API
        fetch('http://localhost:4000/home', {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + accessToken // Include the JWT access token in the Authorization header
            }
        })
            .then(response => {
                if (!response.ok) {
                    navigate('/login'); // Redirect to login page if response is not ok
                    throw new Error('Network response was not ok');
                }
            })

            .catch(error => {
                // Handle any errors that occurred during the fetch request
                console.error('There was a problem with the fetch operation:', error);
            });
    }, [accessToken]); // Add accessToken to the dependency array

    useEffect(() => {
        console.log('second effect')
    }, [])

    const handleFileChange= (e) =>{
        const file = e.target.files[0];

        if(file){
            if(file.type && file.type.startsWith('image'))
                {
                    setPhoto[file];
                }
        }
    }

    const handleLogout = () => {
        // Clear the authentication token from localStorage
        localStorage.removeItem('accessToken');

        // Redirect the user to the login page
        navigate('/login');
    };


    return (
        <>
        <div>
            <form method='POST' encType='multipart/formdata'>
                <input type='file'
                accept='image/*'
                onChange={handleFileChange}
                />
            </form>
        </div>
        <div>
            {photo?photo:null}
        </div>
        <div>
            <button onClick={handleLogout} className="w-fit text-white bg-blue-700 hover:bg-blue-800 focus:outline-none focus:ring-4 focus:ring-blue-300 font-medium rounded-full text-sm px-5 py-2.5 text-center me-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Logout</button>
        </div>
        </>

    );
};

export default Home;
